import React from "react";
import ReactDOM from "react-dom";
import "./styles.css";
import * as Redux from "@reduxjs/toolkit";
import expect from "expect";
import deepFreeze from "deep-freeze";

export default function App() {

   //Reducer handling updating individual todo objects
   const todo = (state = {}, action) => {
      switch (action.type) {
         case 'ADD_TODO':
            return {
               id: action.id,
               text: action.text,
               completed:false
            };
         case 'TOGGLE_TODO':
            if (state.id !== action.id) {
               return state;//Not the todo we're looking for.
            }
            return {
               ...state,
               completed: !state.completed
            };
         default:
            return state;
      }
   }

   // Reducer handling updating the list containing all todo objects
   const todos = (state = [], action) => {
      switch (action.type) {
         case 'ADD_TODO':
            return [
               ...state,
               todo(undefined, action)
            ];
         case 'TOGGLE_TODO':
            return state.map(t => todo(t, action));
         default:
            return state;
      }
   };

   // Reducer handling updating the visibility filter for the app
   const visibilityFilter = (state = 'SHOW_ALL', action) => {
      switch (action.type) {
         case 'SET_VISIBILITY_FILTER':
            return action.filter;
         default:
            return state;
      }
   };

   //MKING MY OWN

   // Reducer handling combining the list of todos with the visibility filter, representing the
   // final UI state
   //const { combineReducers } = Redux;
   const todoApp = combineReducers({
      todos, visibilityFilter
   });

   const { createStore } = Redux;
   const store = createStore(todoApp);



   const testAddTodo = () => {
      const stateBefore = [];
      const action = {
         type: 'ADD_TODO',
         id: 0,
         text: 'Learn Redux'
      };
      const stateAfter = [
         {
            id: 0,
            text: 'Learn Redux',
            completed: false
         }
      ];

      deepFreeze(stateBefore);
      deepFreeze(action);

      expect(
         todos(stateBefore, action)
      ).toEqual(stateAfter);
   };

   const testToggleTodo = () => {
      const stateBefore = [
         {
            id: 0,
            text: 'Learn Redux',
            completed: false
         },
         {
            id: 1,
            text: 'Go shopping',
            completed: false
         }
      ];

      const action = {
         type: 'TOGGLE_TODO',
         id: 1
      };

      const stateAfter = [
         {
            id: 0,
            text: 'Learn Redux',
            completed: false
         },
         {
            id: 1,
            text: 'Go shopping',
            completed: true
         }
      ];

      deepFreeze(stateBefore);
      deepFreeze(action);

      expect(
         todos(stateBefore, action)
      ).toEqual(stateAfter);

   };

   testAddTodo();
   testToggleTodo();
   console.log('All tests passed.')

   return (
      <div className="App">
         <h1>Hello World</h1>
         <h2>Do stuff.</h2>
         <p>And things.</p>
         <p>And more things.</p>
      </div>
   );

}
