import React from "react";
import ReactDOM from "react-dom";
import "./styles.css";
import * as Redux from "@reduxjs/toolkit";
import deepFreeze from "deep-freeze";

/************************************* Reducers ******************************/


//Reducer handling updating individual todo objects
const todo = (state = {}, action) => {
   switch (action.type) {
      case 'ADD_TODO':
         return {
            id: action.id,
            text: action.text,
            completed:false
         };
      case 'TOGGLE_TODO':
         if (state.id !== action.id) {
            return state;//Not the todo we're looking for.
         }
         return {
            ...state,
            completed: !state.completed
         };
      default:
         return state;
   }
}

// Reducer handling updating the list containing all todo objects
const todos = (state = [], action) => {
   switch (action.type) {
      case 'ADD_TODO':
         return [
            ...state,
            todo(undefined, action)
         ];
      case 'TOGGLE_TODO':
         return state.map(t => todo(t, action));
      case 'REMOVE_TODO':
         return state.filter(t => t.id !== action.id);
      default:
         return state;
   }
};

// Reducer handling updating the visibility filter for the app
const visibilityFilter = (state = 'SHOW_ALL', action) => {
   switch (action.type) {
      case 'SET_VISIBILITY_FILTER':
         return action.filter;
      default:
         return state;
   }
};

// Reducer handling combining the list of todos with the visibility filter, representing the
// final UI state
const { combineReducers } = Redux;
const todoApp = combineReducers({
   todos,
   visibilityFilter
});

const { createStore } = Redux;
const store = createStore(todoApp);

/************************************* Event Handlers ******************************/

const handleKeyDown = (e, button) => {
   e = e || Window.event;
   if (e.keyCode === 13) {
      e.preventDefault();
      if (button.hasFocus) return;
      button.focus();
   }
}

const handleKeyUp = (e, button) => {
   e = e || Window.event;
   if (e.keyCode === 13) {
      e.preventDefault();
      button.click();
   }
};

let nextTodoId = 0;
const handleButtonClick = (input) => {
   if (input.value === '' || input.value === undefined) return input.focus();
   return (
      store.dispatch({
         type: 'ADD_TODO',
         text: input.value,
         id: nextTodoId++
      }),
      input.value ='',
      input.focus()
   );
};

const handleLeftClickTodo = (id) => {
   store.dispatch({
      type: 'TOGGLE_TODO',
      id
   })
}

const handleRightClickTodo = (id) => {
   store.dispatch({
      type: 'REMOVE_TODO',
      id: id
   })
}

const handleClickTodo = (e, id) => {
   e = e || Window.event;
   if (e.type) {
      if (e.type === 'click') return handleLeftClickTodo(id);
      if (e.type === 'contextmenu'){
         e.preventDefault();
         return handleRightClickTodo(id);
      }
   }
   else if (e.which) {
      if (e.which === 0) return handleLeftClickTodo(id);
      if (e.which === 1){
         e.stopPropagation() || e.preventDefault();
         return handleRightClickTodo(id);
      }
   }
   else if (e.button) {
      if (e.button === 1) return handleLeftClickTodo(id);
      if (e.button === 2){
         e.stopPropagation() || e.preventDefault();
         return handleRightClickTodo(id);
      }
   }
}

/************************************* Presentation Components ******************************/

const AddTodo = ({
   onAddClick
}) => {
   let input = React.useRef(null);
   let button = React.useRef(null);
   return (
      <div>
         <input
            ref={node => {
               input = node;
            }}
            onKeyUp={(e) => handleKeyUp(e, button)}
            onKeyDown={(e) => handleKeyDown(e, button)}
         />
         <button
            ref={buttonNode => {
               button = buttonNode;
            }}
            onClick={() => onAddClick(input)}
            onKeyDown={(e) => handleKeyDown(e, button)}
            onKeyUp={(e) => handleKeyUp(e, button)}
         >
            Add Todo
         </button>
      </div>
   );
};

const Todo = ({
   onClick,
   completed,
   text,
   onContextMenu,
   id
}) => (
   <li
      onClick={e => onClick(e, id)}
      onContextMenu={e => onContextMenu(e, id)}
      style={{
         textDecoration: completed? 'line-through' : 'none'
      }}
   >
      {text}
   </li>
);


const TodoList = ({
   todos,
   onTodoClick
}) => (
   <ul>
      {todos.map(todo =>
         <Todo
            key={todo.id}
            {...todo}
            onClick={onTodoClick}
            onContextMenu={onTodoClick}
         />
      )}
   </ul>
);

const Footer = ({
   visibilityFilter,
   onFilterClick
}) => {
   const filterObj = {currentFilter: visibilityFilter, onClick: onFilterClick}
   return (
      <p style={{margin:0, marginBottom:'0.2em'}}>
         Show:
         {' '}
         <FilterLink
            filter='SHOW_ALL'
            {...filterObj}
         >
            All
         </FilterLink>
         {' '}
         <FilterLink
            filter='SHOW_ACTIVE'
            {...filterObj}
         >
            Active
         </FilterLink>
         {' '}
         <FilterLink
            filter='SHOW_COMPLETED'
            {...filterObj}
         >
            Completed
         </FilterLink>
      </p>
   );
};

const FilterLink = ({
  filter,
  currentFilter,
  children,
  onClick
}) => {
   if (filter === currentFilter) {
      return <span>{children}</span>;
   }
   return (
      <a
         href='#'
         onClick={e => {
            e.preventDefault();
            onClick(filter);
         }}
      >
         {children}
      </a>
   );
};

/************************************* Container Components ******************************/

const getVisibleTodos = (
   todos,
   filter
) => {
   switch (filter) {
      case 'SHOW_COMPLETED':
         return todos.filter(
            t => t.completed
         );
      case 'SHOW_ACTIVE':
         return todos.filter(
            t => !t.completed
         );
      default:
         return todos;
   }
};

const TodoApp = ({
   todos,
   visibilityFilter
}) => {
   return (
      <div>
         <AddTodo
            onAddClick = {handleButtonClick}
         />
         <TodoList
            todos={getVisibleTodos(
               todos,
               visibilityFilter
            )}
            onTodoClick={handleClickTodo}
         />
         <Footer
            visibilityFilter={visibilityFilter}
            onFilterClick={(filter) => {
               store.dispatch({
                  type: 'SET_VISIBILITY_FILTER',
                  filter
               });
            }}
         />
         <p style={{fontSize:'0.75em', margin:0}}>Left click an item to toggle its completion.</p>
         <p style={{fontSize:'0.75em', margin:0}}>Right click an item to remove it from the list.</p>
      </div>
   );
};

const render = () => {
   ReactDOM.render(
      <TodoApp
         {...store.getState()}
      />, document.getElementById('root')
   );
   document.getElementsByTagName('input')[0].focus();
};

// Render the application, in response to the current application state.
// Calls render every time the store changes.
store.subscribe(render);
// Call render once to render the initial state.
render();

document.onload()
