import React from "react";
import ReactDOM from "react-dom";
import "./styles.css";
import * as Redux from "@reduxjs/toolkit";
import expect from "expect";
import deepFreeze from "deep-freeze";

export default function App() {

   // Reducer function
   const todos = (state = [], action) => {
      switch (action.type) {
         case 'ADD_TODO':
            return [
               ...state,
               {
                  id: action.id,
                  text: action.text,
                  completed:false
               }
            ];
         // Reducers must return the given state, for any unknown/unhandled situatio
         default:
            return state;
      }
   };


   const testAddTodo = () => {
      const stateBefore = [];
      const action = {
         type: 'ADD_TODO',
         id: 0,
         text: 'Learn Redux'
      };
      const stateAfter = [
         {
            id: 0,
            text: 'Learn Redux',
            completed: false
         }
      ];

      deepFreeze(stateBefore);
      deepFreeze(action);

      expect(
         todos(stateBefore, action)
      ).toEqual(stateAfter);
   };

   testAddTodo();
   console.log('All tests passed.')

   return (
      <div className="App">
         <h1>Hello World</h1>
         <h2>Do stuff.</h2>
         <p>And things.</p>
         <p>And more things.</p>
      </div>
   );

}
