import React from "react";
import ReactDOM from "react-dom";
import "./styles.css";
import * as Redux from "@reduxjs/toolkit";
import PropTypes from "prop-types";

/************************************* Reducers ******************************/


//Reducer handling updating individual todo objects
const todo = (state = {}, action) => {
   switch (action.type) {
      case 'ADD_TODO':
         return {
            id: action.id,
            text: action.text,
            completed:false
         };
      case 'TOGGLE_TODO':
         if (state.id !== action.id) {
            return state;//Not the todo we're looking for.
         }
         return {
            ...state,
            completed: !state.completed
         };
      default:
         return state;
   }
}

// Reducer handling updating the list containing all todo objects
const todos = (state = [], action) => {
   switch (action.type) {
      case 'ADD_TODO':
         return [
            ...state,
            todo(undefined, action)
         ];
      case 'TOGGLE_TODO':
         return state.map(t => todo(t, action));
      case 'REMOVE_TODO':
         return state.filter(t => t.id !== action.id);
      default:
         return state;
   }
};

// Reducer handling updating the visibility filter for the app
const visibilityFilter = (state = 'SHOW_ALL', action) => {
   switch (action.type) {
      case 'SET_VISIBILITY_FILTER':
         return action.filter;
      default:
         return state;
   }
};

// Reducer handling combining the list of todos with the visibility filter, representing the
// final UI state
const { combineReducers } = Redux;
const todoApp = combineReducers({
   todos,
   visibilityFilter
});

/************************************* Event Handlers ******************************/

const handleKeyDown = (e, button) => {
   e = e || Window.event;
   if (e.keyCode === 13) {
      e.preventDefault();
      if (button.hasFocus) return;
      button.focus();
   }
}

const handleKeyUp = (e, button) => {
   e = e || Window.event;
   if (e.keyCode === 13) {
      e.preventDefault();
      button.click();
   }
};

let nextTodoId = 0;
const handleButtonClick = (props, context) => {
   const {store} = context;
   const {input} = props;
   if (input.value === '' || input.value === undefined) return input.focus();
   const temp = input.value;
   input.focus();
   input.value = '';
   store.dispatch({
      type: 'ADD_TODO',
      text: temp,
      id: nextTodoId++
   })
};
handleButtonClick.contextTypes = {
   store: PropTypes.object
};

const handleLeftClickTodo = (props, {store}) => {
   const {id} = props;
   store.dispatch({
      type: 'TOGGLE_TODO',
      id
   })
};
handleLeftClickTodo.contextTypes = {
   store: PropTypes.object
};

const handleRightClickTodo = (props, {store}) => {
   const {id} = props;
   store.dispatch({
      type: 'REMOVE_TODO',
      id
   })
   document.getElementsByTagName('input')[0].focus();
};
handleRightClickTodo.contextTypes = {
   store: PropTypes.object
};

const handleClickTodo = (props) => {
   const {id} = props;
   const {e} = props || Window.event;
   if (e.type) {
      if (e.type === 'click') return handleLeftClickTodo({id});
      if (e.type === 'contextmenu'){
         e.preventDefault();
         return handleRightClickTodo({id});
      }
   }
   else if (e.which) {
      if (e.which === 0) return handleLeftClickTodo({id});
      if (e.which === 1){
         e.stopPropagation() || e.preventDefault();
         return handleRightClickTodo({id});
      }
   }
   else if (e.button) {
      if (e.button === 1) return handleLeftClickTodo(id);
      if (e.button === 2){
         e.stopPropagation() || e.preventDefault();
         return handleRightClickTodo({id});
      }
   }
};

/************************************* Presentation Components ******************************/

const AddTodo = ({ divStyle, inputStyle, buttonStyle }) => {
   let input = React.useRef(null);
   let button = React.useRef(null);
   return (
      <div
         style={divStyle}
         onClick={() =>
            input.focus()}
         onLoad={() =>
            input.focus()}
      >
         <input
            style={inputStyle}
            ref={node => {
               input = node;
            }}
            onKeyUp={(e) => handleKeyUp(e, button)}
            onKeyDown={(e) => handleKeyDown(e, button)}
         />
         <button
            style={buttonStyle}
            ref={buttonNode => {
               button = buttonNode;
            }}
            onClick={() => handleButtonClick(input)}
            onKeyDown={(e) => handleKeyDown(e, button)}
            onKeyUp={(e) => handleKeyUp(e, button)}
         >
            Add Todo
         </button>
      </div>
   );
};

const Todo = ({ style, onClick, completed, text, onContextMenu, id }) => {
   return (
      <li
         onClick={e => onClick(e, id)}
         onContextMenu={e => onContextMenu({e, id})}
         style={{
            textDecoration: completed? 'line-through' : 'none',
            style
         }}
      >
         {text}
      </li>
   );
};

const TodoList = ({ listStyle, singleTodoStyle, todos, onTodoClick}) => {
   return (
      <ul style={listStyle}>
         {todos.map(todo =>
            <Todo
               style={singleTodoStyle}
               key={todo.id}
               {...todo}
               onClick={onTodoClick}
               onContextMenu={onTodoClick}
            />
         )}
      </ul>
   );
}

const Footer = (props) => {
   const { footerStyle, selectionStyle, linkStyle } = props;
   return (
      <p style={footerStyle}>
         Show:
         {' '}
         <FilterLink
            selectionStyle={selectionStyle}
            linkStyle={linkStyle}
            filter='SHOW_ALL'
         >
            All
         </FilterLink>
         {' '}
         <FilterLink
            selectionStyle={selectionStyle}
            linkStyle={linkStyle}
            filter='SHOW_ACTIVE'
         >
            Active
         </FilterLink>
         {' '}
         <FilterLink
            selectionStyle={selectionStyle}
            linkStyle={linkStyle}
            filter='SHOW_COMPLETED'
         >
            Completed
         </FilterLink>
      </p>
   );
};
Footer.contextTypes = {
   store: PropTypes.object
};

const Link = (props) => {
   const { selectionStyle, linkStyle, active, children, onClick } = props;
   if (active) {
      return <span style={selectionStyle}>{children}</span>;
   }
   return (
      <a
         style={linkStyle}
         href='#'
         onClick={e => {
            e.preventDefault();
            onClick();
         }}
      >
         {children}
      </a>
   );
};

/************************************* Container Components ******************************/

const { Component } = React;

class VisibleTodoList extends Component {
   componentDidMount() {
      const {store} = this.context;
      this.unsubscribe = store.subscribe(() =>
         this.forceUpdate()
      );
   }
   componentWillUnmount() {
      this.unsubscribe();
   }
   render() {
      const props = this.props;
      const {store} = this.context;
      const state = store.getState();
      return (
         <TodoList
            todos={
               getVisibleTodos(
                  state.todos,
                  state.visibilityFilter
               )
            }
            listStyle={props.listStyle}
            singleTodoStyle={props.singleTodoStyle}
            onTodoClick={handleClickTodo}
         />
      );
   };
}
VisibleTodoList.contextTypes = {
   store: PropTypes.object
};

class FilterLink extends Component {
   componentDidMount() {
      const {store} = this.context;
      this.unsubscribe = store.subscribe(() =>
         this.forceUpdate()
      );
   }
   componentWillUnmount() {
      this.unsubscribe();
   }
   render() {
      const props = this.props;
      const {store} = this.context;
      const state = store.getState();
      return (
         <Link
            active={props.filter === state.visibilityFilter}
            onClick={() =>
               store.dispatch({
                  type: 'SET_VISIBILITY_FILTER',
                  filter: props.filter
               })
            }
         >
            {props.children}
         </Link>
      );
   };
}
FilterLink.contextTypes = {
   store: PropTypes.object
};

const getVisibleTodos = (todos, filter) => {
   switch (filter) {
      case 'SHOW_COMPLETED':
         return todos.filter(
            t => t.completed
         );
      case 'SHOW_ACTIVE':
         return todos.filter(
            t => !t.completed
         );
      default:
         return todos;
   }
};

//using position:'-webkit-sticky', which only works on Safari. Will need to change later
const TodoApp = () => {
   return (
      <div style={{ width:'15em' }}>
         <h1 style={{textAlign:'center', margin:'16px'}}>Todo</h1>
         <AddTodo
            divStyle={{ display:'block', alignContent:'center', margin:0, height:'1.25em',
            width:'100%', position:'-webkit-sticky', left:'0px', top:'0px' }}
            inputStyle={{ height:'calc(100% - 4px)', fontSize:'1em', position:'absolute', left:'0',
               right:'6.5em', border:'1px inset #ccc', margin:0, backgroundColor:'#eee',
               width:'calc(100% - 6.5em' }}
            buttonStyle={{ height:'100%', width:'6em', position:'absolute', right:'0',
               border:'1px outset #aaa', borderRadius:'4px', textAlign:'center',
               backgroundColor:'#eee', fontSize:'1em', padding:'0 0.4em' }}
         />
         <VisibleTodoList
            listStyle={{  }}
            singleTodoStyle={{  }}
         />
         <Footer
            footerStyle={{ textAlign:'center', margin:'3px', display:'block' }}
            selectionStyle={{  }}
            linkStyle={{  }}
         />
         <p style={{textAlign:'center', fontSize:'0.75em', margin:0}}>
            Left click an item to toggle its completion.
         </p>
         <p style={{textAlign:'center', fontSize:'0.75em', margin:0}}>
            Right click an item to remove it from the list.
         </p>
      </div>
   );
};

class Provider extends Component {
   getChildContext() {
      return {
         store: this.props.store
      };
   }
   render() {
      return this.props.children;
   }
}
Provider.childContextTypes = {
   store: PropTypes.object
};

const { createStore } = Redux;

ReactDOM.render(
   <Provider store={createStore(todoApp)}>
      <TodoApp />
   </Provider>, document.getElementById('root')
);

if (document) document.addEventListener('DOMContentLoaded', function() {
   document.getElementsByTagName('input')[0].focus();
   window.onclick = function() {document.getElementsByTagName('input')[0].focus()};
})
