import React from "react";
import ReactDOM from "react-dom";
import "./styles.css";
import * as Redux from "@reduxjs/toolkit";
import expect from "expect";
import deepFreeze from "deep-freeze";

export default function App() {

   // Reducer function
   const todos = (state = [], action) => {
      switch (action.type) {
         case 'ADD_TODO':
            return [
               ...state,
               {
                  id: action.id,
                  text: action.text,
                  completed:false
               }
            ];
         case 'TOGGLE_TODO':

            console.log(...state.slice(0, action.id));
            console.log({
               ...state[action.id],
               completed: !state[action.id].completed
            });
            console.log(...state.slice(action.id + 1));

            return [
               ...state.slice(0, action.id),
               {
                  ...state[action.id],
                  completed: !state[action.id].completed
               },
               ...state.slice(action.id + 1)
            ];
         // Reducers must return the given state, for any unknown/unhandled situations.
         default:
            return state;
      }
   };


   const testAddTodo = () => {
      const stateBefore = [];
      const action = {
         type: 'ADD_TODO',
         id: 0,
         text: 'Learn Redux'
      };
      const stateAfter = [
         {
            id: 0,
            text: 'Learn Redux',
            completed: false
         }
      ];

      deepFreeze(stateBefore);
      deepFreeze(action);

      expect(
         todos(stateBefore, action)
      ).toEqual(stateAfter);
   };

   const testToggleTodo = () => {
      const stateBefore = [
         {
            id: 0,
            text: 'Learn Redux',
            completed: false
         },
         {
            id: 1,
            text: 'Go shopping',
            completed: false
         }
      ];

      const action = {
         type: 'TOGGLE_TODO',
         id: 1
      };

      const stateAfter = [
         {
            id: 0,
            text: 'Learn Redux',
            completed: false
         },
         {
            id: 1,
            text: 'Go shopping',
            completed: true
         }
      ];

      deepFreeze(stateBefore);
      deepFreeze(action);

      expect(
         todos(stateBefore, action)
      ).toEqual(stateAfter);

   };

   testAddTodo();
   testToggleTodo();
   console.log('All tests passed.')

   return (
      <div className="App">
         <h1>Hello World</h1>
         <h2>Do stuff.</h2>
         <p>And things.</p>
         <p>And more things.</p>
      </div>
   );

}
